#Separaçãp em lista de palavras
words_A = textA.split()
words_B = textB.split()

#Definição das letras 'foo'
foo = ['s','l','f', 'w','k']


# Preposições

#Criação de variaveis de contagem de preoposições
preposicoes_A = 0
preposicoes_B = 0

#Contagem
for word in words_A:
    if len(word) == 3:                      #Palavra deve ter 3 letras
        if not 'd' in word:                 #Não deve haver 'd' nela
            if not word[-1] in foo:         #A ultima letra não pode ser 'foo'
                preposicoes_A = preposicoes_A + 1

for word in words_B:
    if len(word) == 3:
        if not 'd' in word:
            if not word[-1] in foo:
                preposicoes_B = preposicoes_B + 1

#Apresentação dos resultados
print('O texto A tem {} preposições.'.format(preposicoes_A))
print('O texto B tem {} preposições.'.format(preposicoes_B))


#Verbos

#Criação das variaveis de verbos e verbos em 1a pessoa
verbos_A = 0
verbos_B = 0
ver1p_A = 0
ver1p_B = 0

#Contagem
for word in words_A:
    if len(word) >= 8:                              #A palavra deve ter mais de 8 letras
        if word[-1] in foo:                         #A ultima letra deve ser 'foo'
            verbos_A = verbos_A + 1
            if word[0] not in foo:                  #Se começar em 'foo', é em primeira pessoa
                ver1p_A = ver1p_A + 1

for word in words_B:
    if len(word) >= 8:
        if word[-1] in foo:
            verbos_B = verbos_B + 1
            if word[0] not in foo:
                ver1p_B = ver1p_B + 1

#Apresentação dos resultados
print('O texto A tem {} verbos, dos quais {} estão em primeira pessoa.'.format(verbos_A,ver1p_A))
print('O texto B tem {} verbos, dos quais {} estão em primeira pessoa.'.format(verbos_B,ver1p_B))


#Ordem alfabética

#Definição da ordem alfabetica
ordemalfabetica = 'kbwrqdnfxjmlvhtcgzps'


# Criação de um dicionario atribuindo valor para cada letra, baseado na ordem alfabética
i = 1
dict_alph = {}
for char in ordemalfabetica:
    dict_alph[char] = i
    i = i+1

#Criação de uma função que retorna o valor da i-nesima posicão na palavra word
# caso i seja muito grande, retorna 0
def num_or_zero(word,i):
    if i >= len(word):
        return 0
    else:
        return dict_alph[word[i]]

#Sorting das palavras
for i in range(15,-1,-1):
    words_A = sorted(words_A,key = (lambda x: num_or_zero(x,i)))
    words_B = sorted(words_B,key = (lambda x: num_or_zero(x,i)))

#Remoção das repetidas
words_A = list(dict.fromkeys(words_A))
words_B = list(dict.fromkeys(words_B))

#Transformando as listas em uma grande string novamente
sorted_A = ''
for word in words_A:
    sorted_A = sorted_A + ' ' + word
    
sorted_B = ''
for word in words_B:
    sorted_B = sorted_B + ' ' + word

#Remoção do espaço inicial
sorted_A = sorted_A[1:]
sorted_B = sorted_B[1:]

#Apresentação dos resultados
print('A lista de palavras no texto A, em ordem: \n')
