#Números bonitos

#Reorganização do dicionario, agora iniciando em 0
i = 0
dict_alph = {}
for char in ordemalfabetica:
    dict_alph[char] = i
    i = i+1

#Definição da função que transforma a palavra em número
def word_to_number(word):
    num = 0
    for i in range(len(word)):
        num = num + (dict_alph[word[i]] * (20**i))
    return num

#Criação de variaveis de contagem de números bonitos
beuty_num_A = 0
beuty_num_B = 0

#Contagem
for word in words_A:
    num = word_to_number(word)              #Transformação de palavra para número
    if num >= 440566:                       #O número deve ser maior ou igual a 440566
        if (num % 3) == 0:                  #O número deve ser divisivel por 3
            beuty_num_A = beuty_num_A + 1
    
for word in words_B:
    num = word_to_number(word)
    if num >= 440566:
        if (num % 3) == 0:
            beuty_num_B = beuty_num_B + 1

#Apresentação dos resultados
print('A lista A tem {} números bonitos'.format(beuty_num_A))
print('A lista B tem {} números bonitos'.format(beuty_num_B))